import { useRef, useState } from 'react';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import './home.css';
import logo from '../../images/oasis-transparent.png';
import InstagramIcon from '@material-ui/icons/Instagram';
import FacebookIcon from '@material-ui/icons/Facebook';
import TwitterIcon from '@material-ui/icons/Twitter';
import CheckRoundedIcon from '@material-ui/icons/CheckRounded';
import DescriptionIcon from '@material-ui/icons/Description';
import SendIcon from '@material-ui/icons/Send';
import emailjs from '@emailjs/browser';

const ScholarshipRequirements = [
	'AB540/DACA Students, Eligible/ Residents and citizens',
	'Must have been enrolled in Kindergarten at Oasis Elementary, beginning with the class of 2009-2010',
	'Students must graduate from an accredited institution within the state of California with a High School Diploma or General Equivalency Diploma.',
	'Students must have verification of admittance to an accredited profit or nonprofit institution providing vocational, occupational, and technical or college education on either full-time or part-time basis.',
	'GPA requirement: 2.0 Cumulative'
]

const Home = () => {

	const [contactName, setName] = useState('');
	const [contactEmail, setEmail] = useState('');
	const [contactMessage, setMessage] = useState('');
	// const [formResponse, setFormResponse] = useState(null);

	const contactForm = useRef();

	const handleNameChange = (event) => {
		setName(event.target.value);
	}

	const handleEmailChange = (event) => {
		setEmail(event.target.value);
	}

	const handleMessageChange = (event) => {
		setMessage(event.target.value);
	}

	const isInvalidEmail = (email) => {
		return email.length > 0 && !String(email).match(/^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/);
	}

	const sendForm = async () => {
		const data = {
			service_id: 'service_rexzi6j',
			template_id: 'template_3uj95nn',
			user_id: 'yfXGSGjBp-1KKRm84',
			tempalte_params: {
				'name': contactName,
				'email': contactEmail,
				'message': contactMessage,
			}
		}

		const fetchResponse = await fetch('https://api.emailjs.com/api/v1.0/email/send', {
			method: 'POST',
			body: JSON.stringify(data),
			mode: 'cors',
			headers: {
				'Content-Type': 'application/json'
			}
		});

		console.log('fetchResponse =', fetchResponse);
	}

	const submitForm = (e) => {
		e.preventDefault();

		let hasNameError = false;
		let hasEmailError = false;
		let hasMessageError = false;

		// make sure there's no errors
		if ( !contactName ) hasNameError = true;
